
public class ForLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// For loop -  print 0 t/m 10
		for(int i = 0; i <= 10; i++) {
			System.out.println(i);
		}
		
		// For loop - print 0 t/m 9
		for(int b = 0; b < 10; b++) {
			System.out.println(b);
		}
		
	}

}
